import { directive } from 'alpinejs/src/directives'
import { warn } from 'alpinejs/src/utils/warn'

import 'alpinejs/src/directives/x-transition'
import 'alpinejs/src/directives/x-modelable'
import 'alpinejs/src/directives/x-teleport'
import 'alpinejs/src/directives/x-ignore'
import 'alpinejs/src/directives/x-effect'
import 'alpinejs/src/directives/x-model'
import 'alpinejs/src/directives/x-cloak'
import 'alpinejs/src/directives/x-init'
import 'alpinejs/src/directives/x-text'
import 'alpinejs/src/directives/x-html'
import 'alpinejs/src/directives/x-bind'
import 'alpinejs/src/directives/x-data'
import 'alpinejs/src/directives/x-show'
import 'alpinejs/src/directives/x-for'
import 'alpinejs/src/directives/x-ref'
import 'alpinejs/src/directives/x-if'
import 'alpinejs/src/directives/x-id'
import 'alpinejs/src/directives/x-on'

// Register warnings for people using plugin syntaxes and not loading the plugin itself:
warnMissingPluginDirective('Collapse', 'collapse', 'collapse')
warnMissingPluginDirective('Intersect', 'intersect', 'intersect')
warnMissingPluginDirective('Focus', 'trap', 'focus')
warnMissingPluginDirective('Mask', 'mask', 'mask')

function warnMissingPluginDirective(name, directiveName, slug) {
    directive(directiveName, (el) => warn(`You can't use [x-${directiveName}] without first installing the "${name}" plugin here: https://alpinejs.dev/plugins/${slug}`, el))
}
