import { directive, prefix } from "alpinejs/src/directives";
import { addInitSelector } from "alpinejs/src/lifecycle";
import { skipDuringClone } from "alpinejs/src/clone";

addInitSelector(() => `[${prefix('init')}]`)

directive('init', skipDuringClone((el, { expression }, { evaluate }) => {
    if (typeof expression === 'string') {
        return !! expression.trim() && evaluate(expression, {}, false)
    }

    return evaluate(expression, {}, false)
}))
