import { directive } from 'alpinejs/src/directives'
import { mutateDom } from 'alpinejs/src/mutation'

directive('text', (el, { expression }, { effect, evaluateLater }) => {
    let evaluate = evaluateLater(expression)

    effect(() => {
        evaluate(value => {
            mutateDom(() => {
                el.textContent = value
            })
        })
    })
})
