import { warn } from 'alpinejs/src/utils/warn'
import { magic } from 'alpinejs/src/magics'

import 'alpinejs/src/magics/$nextTick'
import 'alpinejs/src/magics/$dispatch'
import 'alpinejs/src/magics/$watch'
import 'alpinejs/src/magics/$store'
import 'alpinejs/src/magics/$data'
import 'alpinejs/src/magics/$root'
import 'alpinejs/src/magics/$refs'
import 'alpinejs/src/magics/$id'
import 'alpinejs/src/magics/$el'

// Register warnings for people using plugin syntaxes and not loading the plugin itself:
warnMissingPluginMagic('Focus', 'focus', 'focus')
warnMissingPluginMagic('Persist', 'persist', 'persist')

function warnMissingPluginMagic(name, magicName, slug) {
    magic(magicName, (el) => warn(`You can't use [$${magicName}] without first installing the "${name}" plugin here: https://alpinejs.dev/plugins/${slug}`, el))
}
